import React from 'react'

const Chat = ({username,room,setUsername,setRoom,setChatScreen,socket}) => {

  //sendroom a click ettiğim zaman socket üzerinden message adında emit et 
    const sendRoom = () => {
        socket.emit('message')
    }

  return (
    <div className='flex items-center justify-center h-full'>
        <div className='w-1/3 h-[300px] bg-rose-100 flex flex-col space-y-4 p-3'>
            <h1 className='text-center my-4 font-bold text-2xl'> CHATE HOŞGELDİNİZ </h1>
            <input value={username} onChange={e => setUsername(e.target.value)} className='h-12 rounded-xl p-3 outline-none' type="text" placeholder='Username' />
            <input value={username} onChange={e => setUsername(e.target.value)} className='h-12 rounded-xl p-3 outline-none' type="text" placeholder='Room' />
            <div onClick={sendRoom} className='bg-rose-500	 text-white cursor-pointer hover:opacity-70 h-12 pt-2 text-xl text-center rounded-xl' >CHAT!!!</div>
        </div>
    </div>
  )
}

export default Chat
