import React, { useEffect, useState } from 'react';

const Chat = ({ socket, username, room }) => {
  const [message, setMessage] = useState('');
  const [messageList, setMessageList] = useState([]);

  useEffect(() => {
    // Sunucudan gelen mesajları dinle
    socket.on('messageReturn', (data) => {
      setMessageList(data);
    });

    // Component'in temizlenmesi durumunda soket bağlantısını kapat
    return () => {
      socket.off('messageReturn');
    };
  }, [socket]);

  const sendMessage = () => {
    const messageContent = {
      username: username,
      message: message,
      room: room,
      date: new Date().getHours() + ':' + new Date().getMinutes(),
    };

    // Mesajı sunucuya gönder
    socket.emit('message', messageContent);

    // Mesajı yerel mesaj listesine ekle
    setMessageList((prev) => [...prev, messageContent]);

    // Mesaj alanını temizle
    setMessage('');
  };

  return (
    <div className='flex items-center justify-center h-full'>
      <div className='w-1/4 h-[500px] bg-rose-100 relative'>
        <div className='w-full h-17 bg-gray-600 flex item-center p-3'>
          <div className='w-12 h-12 bg-black flex rounded-full'></div>
        </div>
        <div className='w-full h-[400px] overflow-y-auto'>
          {messageList.map((msg, i) => (
            <div
              key={i}
              className={`${username === msg.username ? 'flex justify-end' : ''}`}
            >
              <div
                className={`${
                  username === msg.username ? 'bg-green-600' : 'bg-blue-600'
                } w-2/3 h-12 p-2 text-black m-2 rounded-xl rounded-br-none`}
              >
                <div>{msg.message}</div>
                <div className='w-full flex justify-end text-xs'>{msg.username}</div>
              </div>
            </div>
          ))}
        </div>
        <div className='absolute bottom-0 left-0 w-full'>
          <input
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            className='w-3/4 h-12 border p-3 outline-none'
            type='text'
            placeholder='Mesaj gönder'
          />
          <button
            onClick={sendMessage}
            className='w-1/4 bg-rose-600 text-white h-12 hover:opacity-70'
          >
            GÖNDER
          </button>
        </div>
      </div>
    </div>
  );
};

export default Chat;
